(2189, '_site_transient_update_plugins', 'O:8:"stdClass":5:{s:12:"last_checked";i:1611291493;s:7:"checked";a:7:{s:30:"advanced-custom-fields/acf.php";s:5:"5.8.7";s:29:"acf-repeater/acf-repeater.php";s:5:"2.1.0";s:33:"classic-editor/classic-editor.php";s:3:"1.5";s:36:"contact-form-7/wp-contact-form-7.php";s:5:"5.1.6";s:43:"custom-post-type-ui/custom-post-type-ui.php";s:5:"1.7.2";s:51:"motopress-hotel-booking/motopress-hotel-booking.php";s:5:"3.9.2";s:24:"wordpress-seo/wp-seo.php";s:4:"12.8";}s:8:"response";a:2:{s:30:"advanced-custom-fields/acf.php";O:8:"stdClass":12:{s:2:"id";s:36:"w.org/plugins/advanced-custom-fields";s:4:"slug";s:22:"advanced-custom-fields";s:6:"plugin";s:30:"advanced-custom-fields/acf.php";s:11:"new_version";s:5:"5.9.4";s:3:"url";s:53:"https://wordpress.org/plugins/advanced-custom-fields/";s:7:"package";s:71:"https://downloads.wordpress.org/plugin/advanced-custom-fields.5.9.4.zip";s:5:"icons";a:2:{s:2:"2x";s:75:"https://ps.w.org/advanced-custom-fields/assets/icon-256x256.png?rev=1082746";s:2:"1x";s:75:"https://ps.w.org/advanced-custom-fields/assets/icon-128x128.png?rev=1082746";}s:7:"banners";a:2:{s:2:"2x";s:78:"https://ps.w.org/advanced-custom-fields/assets/banner-1544x500.jpg?rev=1729099";s:2:"1x";s:77:"https://ps.w.org/advanced-custom-fields/assets/banner-772x250.jpg?rev=1729102";}s:11:"banners_rtl";a:0:{}s:6:"tested";s:3:"5.6";s:12:"requires_php";s:3:"5.6";s:13:"compatibility";O:8:"stdClass":0:{}}s:33:"classic-editor/classic-editor.php";O:8:"stdClass":12:{s:2:"id";s:28:"w.org/plugins/classic-editor";s:4:"slug";s:14:"classic-editor";s:6:"plugin";s:33:"classic-editor/classic-editor.php";s:11:"new_version";s:3:"1.6";s:3:"url";s:45:"https://wordpress.org/plugins/classic-editor/";s:7:"package";s:61:"https://downloads.wordpress.org/plugin/classic-editor.1.6.zip";s:5:"icons";a:2:{s:2:"2x";s:67:"https://ps.w.org/classic-editor/assets/icon-256x256.png?rev=1998671";s:2:"1x";s:67:"https://ps.w.org/classic-editor/assets/icon-128x128.png?rev=1998671";}s:7: